import axios from "axios";

const endpointURL = "http://localhost:4000/graphql";

export async function loadJobs() {
  const response = await axios({
    url: endpointURL,
    method: "post",
    data: {
      query: `
        {
            jobs {
              id
              title

              company {
                id
                name

              }
            }
          }`,
    },
  });
  return response.data
  ;
}
